---@class Weapon
---@field id number
---@field name string
---@field hashKey string
---@field ammoType number
---@field descriptions string
---@field modelHashKey string
---@field componentsAllowed table<number, number>
---@field tints table<number, number>
---@field liveryColors table<number, number>
---@field price number
Weapon = {}
Weapon.__index = Weapon

---@class WeaponComponent
---@field id number
---@field name string
---@field hashKey string
---@field descriptions string
---@field modelHashKey string
WeaponComponent = {}
WeaponComponent.__index = WeaponComponent

---@type Weapon[]
local WEAPONS = {};

---@type WeaponComponent[]
local WEAPONS_COMPONENTS = {};

WEAPONS_LISTS = {};

setmetatable(Weapon, {
	__call = function(_, id, name, hashKey, ammoType, descriptions, modelHashKey, componentsAllowed, tints, liveryColors, price)
		local self = setmetatable({}, Weapon);

		self.id = id;
		self.name = name;
		self.hashKey = hashKey
		self.ammoType = ammoType;
		self.descriptions = descriptions;
		self.modelHashKey = modelHashKey;
		self.componentsAllowed = componentsAllowed;
		self.tints = tints;
		self.liveryColors = liveryColors;
		self.price = price;

        table.insert(WEAPONS_LISTS, { Name = name, Value = hashKey })

		if (WEAPONS[id]) then
			error("Weapon is already registered with this id [" .. id .. "]")
		end

		WEAPONS[id] = self;

		return self;
	end
})

setmetatable(WeaponComponent, {
	__call = function(_, id, name, hashKey, descriptions, modelHashKey)
		local self = setmetatable({}, WeaponComponent);

		self.id = id;
		self.name = name;
		self.hashKey = hashKey;
		self.descriptions = descriptions;
		self.modelHashKey = modelHashKey;

		if (WEAPONS_COMPONENTS[self.id]) then
			error("Weapon component is already register with this id [" .. self.id .. "]")
		end

		WEAPONS_COMPONENTS[self.id] = self;

		return self;
	end
})

---New
---@param id number
---@param name string
---@param weaponHash string
---@param ammoType number
---@param descriptions string
---@param modelHashKey string
---@param componentsAllowed table<number, number>
---@param tints table<number, number>
---@param liveryColors table<number, number>
---@param price number
---@public
---@return void
function Weapon:New(id, name, weaponHash, ammoType, descriptions, modelHashKey, componentsAllowed, tints, liveryColors, price)
	Weapon(id, name, weaponHash, ammoType, descriptions, modelHashKey, componentsAllowed, tints, liveryColors, price);
end

---GetListsByIds
---@param lists table<number, number>
---@public
---@return Weapon[]
function Weapon:GetListsByIds(lists)
	local response = {};
	for i, v in pairs(WEAPONS) do
		if (CHelper:HasValue(lists, v.id)) then
			table.insert(response, v);
		end
	end
	return response;
end

---GetById
---@param id number
---@public
---@return Weapon
function Weapon:GetById(id)
	return WEAPONS[id];
end

---New
---@param id number
---@param name string
---@param hashKey string
---@param descriptions string
---@param modelHashKey string
---@public
---@return void
function WeaponComponent:New(id, name, hashKey, descriptions, modelHashKey)
	WeaponComponent(id, name, hashKey, descriptions, modelHashKey);
end

